import axios from "axios";

let gameID = localStorage.getItem('gameID')

 export default{
    createGame: (gameName) => {
        // console.log("Running the browser");
        axios.post("/api/newgame", {gameName: gameName})
        .then(result => {
            // console.log(result);
            console.log(result.data._id);
            localStorage.setItem("gameID", result.data._id);
        });
    },
    pullGame: (gameID) =>{
        axios.get("/api/pullgame", {_id:gameID})
    }
 };//END export default
