import React, { Fragment,Component } from "react";
import { Link } from "react-router-dom";
import Jumbotron from "../Jumbotron/Jumbotron";
import "../CreateGame/CreateGame.css";
import API from "../../utils/API";

class JoinGame extends Component {

    //If type ID brings up a result from API.pullgame
        //then Create a Team
        //then Insert team into game record
        //then move user along into the game
    state = {
        teamID: ""
    }

    handleJoinGame = () => {
        API.pullGame(this.props.gameID)
            .then(res => {
                if(res){
                    //Create a Team
                    API.createTeam(this.props.gameID)
                        .then (res => {
                            this.setState ({
                                teamID: res.data._id
                            });
                            console.log("Team Created: " + this.state.teamID)
                        })
                    //then Insert team into game record
                    //then move user along into the game
                }
                // else{

                // }
            });
    }



    render(){
        return(
            <Fragment>
                <Jumbotron />
                <div className="Join">
                    <h4 className="login-header">Join a Game</h4>
                        <label>Game ID:</label>
                        <input className="form-control" id="game-id" onBlur={this.props.handleCaptureGameID}></input>
                    <br />
                    {/* <Link to='/auth/google'> */}
                    <button onClick={this.handleJoinGame} type="button" className="btn btn-success" id="play-btn">Let's Play</button>
                    {/* </Link> */}
                    <p className="login-links">
                        Or <Link to="/">Create a Game </Link> 
                    </p>
                </div>
            </Fragment>
        )
    }
};

export default JoinGame;
