import React, {Component, Fragment} from "react";
import axios from "axios";
import "./Challenge.css";
import Challenges from "../TeamDash/challenges.json";

const index = this.props.location.state.challID-1;

class Challenge extends Component {
    state = {
        selectedImg: null
    }

    componentDidMount(props){
    }

//====================CLOUDINARY FUNCTIONS====================
    //function that sets state to selected image (before upload)
    handleImgSelect = event => {
        this.setState({
            selectedImg: event.target.files[0]
        })
    }

    //function that uploads selected image to cloudinary
    handleImgUpload = () => {
        const cloudinaryURL = "https://api.cloudinary.com/v1_1/de3mokru1/upload";
        const cloudinaryUploadPreset = "wphwy703";
        let formData = new FormData();
        formData.append("file", this.state.selectedImg);
        formData.append("upload_preset", cloudinaryUploadPreset);

        axios({
            url: cloudinaryURL,
            method: "POST",
            headers: {
                "Content-Type": "application/x-www-form-urlencoded"
            },
            data: formData
        }).then( (res) => {
            console.log(res.data.secure_url);
        }).catch( (err) => {
            console.error(err);
        });
    }
//====================END CLOUDINARY FUNCTIONS====================

        handleStatus = (complete) => {
            if(complete){
                let status = "Completed";
                return status;
            }
            else {
                let status = "Not Completed";
                return status;
            }
        }

    render(){
        return ( 
        <Fragment>
            <div className="page-header">
                <h5>{Challenges[index].title}</h5>
            </div>
            <div className="challenge-view">
                <div className="challenge-properties">
                    <p className="status"><b>Status:</b> {this.handleStatus(`${Challenges[index].complete}`)}</p>
                    <p className="challenge-desc">{Challenges.description}</p>
                </div>
                <div className="img-upload">
                <input type="file"onChange={this.handleImgSelect} />
                <button onClick={this.handleImgUpload}>Upload</button>
                </div>
            </div>
        </Fragment>
        )
    }
}

export default Challenge;
