import axios from "axios";

 export default{
    createGame: (gameName) => {
        // console.log("Running the browser");
        axios.post("/api/newgame", {gameName: gameName})
        .then(result => {
            console.log(result);
            // console.log(result.data._id);
            localStorage.setItem("gameID", result.data._id);
        });
    },
    pullGame: (gameID) => {
        // axios.post("/api/pullgame/", {_id: gameID})
        return axios.get("/api/pullgame/" + decodeURIComponent(gameID))
        // .then(result => {
        //     // console.log(result.data.gameName);
        //     return result.data.gameName;
        // });
    }
 };//END export default
