import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";
import Jumbotron from "../Jumbotron/Jumbotron";
import "../CreateGame/CreateGame.css";
import API from "../../utils/API";


class GoogleButton extends Component {

    state = {
        teamID: ""
    }

handleCreateTeam = () => {
    API.createTeam(this.props.gameID)
        .then( res => {
            this.setState ({
                teamID: res.data._id
            });
        })
        .then( () => {
            API.insertTeam(this.props.gameID, this.state.teamID)
        })
}


    render(){
        return (
            <Fragment>
            <Jumbotron />
            <div className = "btnDiv">
                <button type= "button" className= "btn btn-success" id="google-btn" value={this.props.gameID} onClick={this.handleCreateTeam}>
                    {/* <a href="http://localhost:8080/auth/google">Login with Google</a> */}
                    Login with Google
                </button>
            </div>
            </Fragment>

        )
    }

};

export default GoogleButton;
