import React, { Component, Fragment } from "react";
import { Link } from "react-router-dom";
import Jumbotron from "../Jumbotron/Jumbotron";
import "./CreateGame.css";
//API calls
import API from "../../utils/API";

class CreateGame extends Component {

    state = {
        gameName: ""
    }

    //Will create game and then set state to is logged in
  handleCreateGame = () => {
    API.createGame(this.state.gameName);
  }

  captureGameName = (event) => {
    let gameName = event.target.value;
    this.setState({
      gameName: gameName
    })
  };


    render(){
        return(
            <Fragment>
            <Jumbotron />
            <div className="Create">
                <h4 className="login-header">Create a Game</h4>
                    <label>Game Name:</label>
                    <input className="form-control" id="game-name" name="gameName" onBlur={this.captureGameName}></input>
                    <button onClick={this.handleCreateGame} onClick={this.props.handleLogin} type="button" className="btn btn-success" id="play-btn">Let's Play </button>
                    {/* <Link to='/auth/google'> </Link> */}
                <p className="login-links">
                    Or <Link to="/join"> Join a Game </Link>
                </p>
            </div>
            </Fragment>
        )
    }
}

export default CreateGame;
